import jwt from 'jsonwebtoken';
require('dotenv').config();

const verifyToken = (req,res,next)=> {
    const token = req.headers.authorization;
    if (!token) {
        return res.status(403).json({ error: 'user unauthorized' })
    }

    jwt.verify(token, process.env.JWT_SECRET, (error, authData)=> {
        if (error) {
            return res.status(403).json({ error: 'unauthorized' })
        }
        req.decoded = authData
        console.log(req.decoded)
        next();
    })
}

export const verifyAsAdmin = (req,res,next)=> {
    if (!req.decoded.isAdmin) {
        return res.status(403).json({ error: 'user unauthorized' })
    }
    next();
}

export const verifyAsUser = (req,res,next)=> {
    if (!req.decoded) {
        return res.status(403).json({ error: 'user unauthorized' })
    }
    next();
}

export default verifyToken;

import express from 'express';
import productController from '../controllers/product';
import productValidation from '../middlewares/validations/productValidation';
import { upload } from '../storage';

const router = express.Router();

const { createProductValidation } = productValidation;

const { createProduct, getProducts, getOneProduct } = productController;

router.post('/createProduct', upload.single('image'), createProductValidation, createProduct);
router.get('/getProducts', getProducts);
router.get('/product/:productId', getOneProduct);

module.exports = router;

import Product from '../model/Product';

const productController = {
    createProduct: async (req, res, next) => {
        try {
            const newProduct = await Product.create({...req.body, image: req.file.path})
            return res.status(200).json(newProduct)
        } catch (error) {
            return res.status(500).json({error: 'internal server eror'})
        }
        next()
    },

    getProducts: async (req,res)=> {
        try {
            const products = await Product.getAllProducts()
            return res.status(200).json(products)
        } catch (error) {
            console.log(error)
            return res.status(500).json({error: 'internal server eror'})
        }
    },

    getOneProduct: async (req,res)=> {
        try {
            const foundProduct = await Product.getById(req.params.productId)
            if (!foundProduct) {
                return res.status(404).json({error: 'product not found'})
            }
            return res.status(200).json(foundProduct)
        } catch (error) {
            return res.status(500).json({error: 'internal server error', stack: error})
        }
    }
}

export default productController;

import db from '../db';
import logger from '../services/logger';

class Product {
    async create(data) {
        const createProduct = `INSERT INTO products (
            "title",
            "price",
            "description",
            "quantity",
            "status",
            "count",
            "producedBy",
            "image"
        )
        VALUES($1,$2,$3,$4,$5,$6,$7,$8)
        returning *`;
        const values = [
            data.title,
            data.price,
            data.description,
            data.quantity,
            data.status,
            data.count,
            data.producedBy,
            data.image
        ]
        try {
            const { rows } = await db.query(createProduct, values)
            return rows[0]
        } catch (error) {
            return error
        }
    }

    async getById(id) {
        const text = `SELECT * FROM products WHERE id = $1`;
        try {
            const {rows} = await db.query(text, [id]);
            return rows[0];
        } catch (error) {
            logger.info(error)
            return error
        }
    }
    
    async getByField(field, value) {
        const text = `SELECT * FROM products WHERE "${field}" = $1`;
        try {
            const {rows} = await db.query(text, [value]);
            return rows[0];
        } catch (error) {
            logger.info(error)
            return error
        }
    }

    async getAllProducts() {
        const text = `SELECT * FROM products`;
        try {
            const {rows} = await db.query(text);
            return rows;
        } catch (error) {
            logger.info(error)
            return error
        }
    }
}

export default new Product();

const productValidation = {
